import { mockRentBike } from '../../mockRentBike/mockRentBike'
import {  mockList } from '../../mockList/mockList'
import { removeElementById } from '../../Utils/bikeUtils'

const initialState = {
    list: mockList,
    rent: mockRentBike
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'CREATE_BIKE':{
            return {
                ...state
            }
        }
        case 'REMOVE_ITEM':{
            return {
                ...state,
                list: removeElementById(action.itemId, state.list)
            }
        }
        case 'SELECT_ITEM_FOR_RENT': {
            return {
                ...state,
            }
        }
        default:
            return state
    }
};

export default reducer;
