import { getBikes as getBikesResource, createItem as createItemResource } from '../../Resources/bike';

export const onRemoveItem = (id) => {
    return {
        type: 'REMOVE_ITEM',
        itemId: id
    }
};

export const onSelectItem = (id) => {
    return {
        type: 'SELECT_ITEM_FOR_RENT',
        itemId: id
    }
};

export const endOfRent = (id) => {
    return {
        type: 'END_OF_RENT',
        itemId: id
    }
};

export const createItem = (item) => async (dispatch, getState) => {
    dispatch({
        type: 'CREATE_ITEM',
        subtype: 'loading'
    })
    createItemResource(item).then(res => {
        const state = getState();
        const newList = [...state.listBike.list, ...res]
        console.log('NEW_LIST: ', newList)
        dispatch({
            type: 'CREATE_ITEM',
            subtype: 'success',
            newList
        })
    },
    (createItemError) => {
        dispatch({
            type: 'CREATE_ITEM',
            subtype: 'failed',
            error: createItemError.message
        })
    })
}
export const getBikes = () => async (dispatch, getState) => {
        dispatch({
            type: 'GET_BIKES',
            subtype: 'loading'
        })
        getBikesResource().then(res => {
            dispatch({
                type: 'GET_BIKES',
                subtype: 'success',
                list: res
            })
        },
        (getBikeListError) => {
            dispatch({
                type: 'GET_BIKES',
                subtype: 'failed',
                error: getBikeListError.message
            })
        })
    };
