import React, { useState } from 'react';

import './RentBikeItem.css';

const RentBikeItem = ({ items, onCancel, time }) => {

    const [countTime, setCountTime] = useState(0);
    const [countOneMin, setCountOneMin] = useState(0);
    const [countTenMin, setCountTenMin] = useState(0);
    const [countOneHour, setCountOneHour] = useState(0);
    const [countTenHour, setCountTenHour] = useState(0);


    const startTime = (boolean) => {
        if (boolean) {
            let timeLeft = countTime;
            let oneMin = countOneMin;
            let tenMin = countTenMin;
            let oneHour = countOneHour;
            let tenHour = countTenHour
            let time = setInterval(() => {
                timeLeft++
                if (timeLeft === 60) {
                    timeLeft = 0
                    oneMin++
                    setCountOneMin(oneMin)
                } else if (oneMin === 10) {
                    timeLeft = 0;
                    oneMin = 0;
                    setCountOneMin(oneMin)
                    tenMin++
                    setCountTenMin(tenMin)
                } else if (tenMin === 6) {
                    timeLeft = 0;
                    oneMin = 0;
                    tenMin = 0;
                    setCountOneMin(oneMin)
                    setCountTenMin(tenMin)
                    oneHour++
                    setCountOneHour(oneHour)
                } else if (oneHour === 10) {
                    timeLeft = 0;
                    oneMin = 0;
                    tenMin = 0;
                    oneHour = 0
                    setCountOneMin(oneMin)
                    setCountTenMin(tenMin)
                    setCountOneHour(oneHour)
                    tenHour++
                    setCountTenHour(tenHour)
                } else if (oneHour === 2 && tenHour === 1) {
                    timeLeft = '-';
                    oneMin = '-';
                    tenMin = '-';
                    oneHour = '-';
                    tenHour = '-'
                    setCountOneMin(oneMin)
                    setCountTenMin(tenMin)
                    setCountOneHour(oneHour)
                    setCountTenHour(tenHour)
                    clearInterval(time)
                }
                setCountTime(countTime)
                console.log(timeLeft)
            }, 1)
        }
    }

    startTime()

    const { name, type, price } = items;

    return (
        <div className="rentBikeItem itemBike d-flex">
            <p>{`${name} / ${type} / $${price}`}</p>
            <span>{countTenHour}{countOneHour}:{countTenMin}{countOneMin}:{countTime}</span>
            <button className="btn btn-danger float-right" onClick={onCancel}>Cancel rent</button>
        </div>
    );
};

export default RentBikeItem;
