import React, { useCallback } from 'react';
import { shallowEqual, useSelector, useDispatch } from 'react-redux';

import './CreateForm.css';

import * as createFormAction from '../../Store/actions/createFormAction'

import { dropDownList } from '../../dropDownList/dropDownList'

const CreateForm = () => {

    const dispatch = useDispatch()
    const form = useSelector(state => state.createForm, shallowEqual)

    const onInputChange = (e) => {
        dispatch(createFormAction.createForm({
            [e.target.name]: e.target.value
        }))
    }

    console.log('STATE: ', form)

    const element = dropDownList.map(({ id, name }) => {
        return (
            <option key={id} value={name}>
                {name}
            </option>
        );
    })

    return (
        <div className="createForm">
            <h3 className="font-weight-bold">Create new rent</h3>
            <form className="form">
                <input type="text"
                    className="formNameBike"
                    name="title"
                    placeholder="Name of the bike" 
                    onChange={onInputChange}
                    value={form.title} 
                />
                <select className="dropDownList" name="dropDownList">
                    {element}
                </select>
                <input type="number"
                    className="formBikeName"
                    name="price"
                    placeholder="Price of the bike"
                    onChange={onInputChange}
                    value={form.price} 
                />
                <button className="btn btn-success">Submit rent</button>
            </form>
        </div>
    )
};

export default CreateForm;
