import { getBikes as geBikesResource } from '../../Resources/bike'

export const onRemoveItem = (id) => {
    return {
        type: 'REMOVE_ITEM',
        itemId: id
    }
};

export const onSelectItem = (id) => {
    return {
        type: 'SELECT_ITEM_FOR_RENT',
        itemId: id
    }
};

export const endOfRent = (id) => {
    return {
        type: 'END_OF_RENT',
        itemId: id
    }
};

export const createItem = (form) => {
    return {
        type: 'CREATE_ITEM',
        item: form
    }
}

export const getBikesReceived = (list) => {
    return {
        type: 'GET_BIKES',
        list
    }
};

export const getBikesFailed = (error) => {
    return {
        type: 'GET_BIKES_FAILED',
        error
    }
};

export const getBikes = () => {
    return async (dispatch, getState) => {
        geBikesResource().then(res => {
            dispatch(getBikesReceived(res))
        },
        (getBikeListError) => {
            dispatch(getBikesFailed(getBikeListError.message))
        })
    };
};
