import { removeElementById, addItemToList } from '../../Utils/bikeUtils'

const initialState = {
    list: [],
    rent: [],
    isLoading: null,
    getBikesError: null,
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'GET_BIKES_LOADING': {
            return {
                ...state,
                isLoading: true
            }
        }
        case 'GET_BIKES_SUCCESS': {
            return {
                ...state,
                list: action.list,
                isLoading: false
            }
        }
        case 'GET_BIKES_FAILED': {
            return {
                ...state,
                getBikesError: action.error,
                isLoading: false
            }
        }
        case 'CREATE_ITEM': {
            const newList =  addItemToList(action.item, state.list)
            return {
                ...state,
                list: newList,
            }
        }
        case 'REMOVE_ITEM':{
            const newList = removeElementById(action.itemId, state.list)
            return {
                ...state,
                list: newList,
            }
        }
        case 'SELECT_ITEM_FOR_RENT': {
            return {
                ...state,
                rent: [
                    ...state.rent,
                    {
                        id: action.itemId,
                        start: Date.now()
                    }
                ],
                
            }
        }
        case 'END_OF_RENT': {
            return {
                ...state,
                rent: state.rent.filter((el) => el.id !== action.itemId),
            }
        }
        default:
            return state
    }
};

export default reducer;
