export const removeElementById = (id, items) => {
    return items.filter((el) => el.id !== id)
};

export const findElementById = (id, items) => {
    return items.find((el) => el.id === id)
};

export const selectElement = (id, items) => {
    const idx = items.findIndex(el => el.id === id);
    return {
        ...items.slice(0, idx),
        ...items.slice(idx + 1)
    }
};

export const addElement = (item, list) => {
    return [ ...list, item]
}
