const initialState = {
    name: '',
    type: 'Road',
    price: ''
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'UPDATE_FORM': {
            const changeNumber = {...action.form, price: action.form.price.toFixed(2)}
            return {
                ...state,
                ...changeNumber
            }
        }
        case 'CREATE_ITEM':
        case 'CLEAR_FORM':
            return {
                ...initialState
            }
        default:
            return state
    }
}

export default reducer;
