import { getBikes as geBikesResource } from '../../Resources/bike'

export const onRemoveItem = (id) => {
    return {
        type: 'REMOVE_ITEM',
        itemId: id
    }
};

export const onSelectItem = (id) => {
    return {
        type: 'SELECT_ITEM_FOR_RENT',
        itemId: id
    }
};

export const endOfRent = (id) => {
    return {
        type: 'END_OF_RENT',
        itemId: id
    }
};

// export const createItem = (form) => {
//     return {
//         type: 'CREATE_ITEM',
//         item: form
//     }
// }

export const createItem = () => async (dispatch, getState) => {
    dispatch
}
export const getBikes = () => async (dispatch, getState) => {
        dispatch({
            type: 'GET_BIKES',
            subtype: 'loading'
        })
        geBikesResource().then(res => {
            dispatch({
                type: 'GET_BIKES',
                subtype: 'success',
                list: res
            })
        },
        (getBikeListError) => {
            dispatch({
                type: 'GET_BIKES',
                subtype: 'failed',
                error: getBikeListError.message
            })
        })
    };
