import { mockRentBike } from '../../mockRentBike/mockRentBike'
import {  mockList } from '../../mockList/mockList'
import { removeElementById, addElement, selectElement, findElementById, addItemToList, getTotal } from '../../Utils/bikeUtils'

const initialState = {
    list: mockList,
    rent: [],
    total: 0,
    quantity: mockList.length,
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'CREATE_ITEM': {
            const newList =  addItemToList(action.item, state.list)
            return {
                ...state,
                list: newList,
                quantity: newList.length,
            }
        }
        case 'REMOVE_ITEM':{
            const newList = removeElementById(action.itemId, state.list)
            return {
                ...state,
                list: newList,
                quantity: newList.length,
            }
        }
        case 'SELECT_ITEM_FOR_RENT': {
            return {
                ...state,
                rent: [
                    ...state.rent,
                    {
                        id: action.itemId,
                        start: Date.now()
                    }
                ],
                total: getTotal(...state.rent)
            }
        }
        case 'END_OF_RENT': {
            return {
                ...state,
                rent: state.rent.filter((el) => el.id !== action.itemId),
                total: getTotal(state.rent.filter((el) => el.id !== action.itemId))
            }
        }
        default:
            return state
    }
};

export default reducer;
