const initialState = {
    title: '',
    dropDownList: 'Road',
    price: ''
}

const reducer = (state = initialState, action) => {
    console.log('STATE: ', state)
    switch (action.type) {
        case 'UPDATE_FORM':
        return {
            ...state,
            ...action.newForm
        }
        case 'CLEAR_FORM': 
        case 'CREATE_ITEM':
            return {
                ...initialState
            }
        default:
            return state
    }
}

export default reducer;
