import React from 'react';
import { useSelector } from 'react-redux';

import './ListBikeItem.css';

const ListBikeItem = ({ onDelete, onSelect, items }) => {

    const isLoadingButton = useSelector(state => state.listBike.removeItemLoading)

    const { name, type, price } = items;

    const isLoading = isLoadingButton ? <p>...</p> : <div className="listBikeItemButton">
        <button className="btn btn-danger float-right" onClick={onDelete}>Delete</button>
        <button className="btn btn-primary float-right" onClick={onSelect}>Delete</button>
</div>

    return (
        <div className="listBikeItem itemBike d-flex">
            <div>{`${name} / ${type} / $${price}`}</div>
            {isLoading}
        </div>
    );
};

export default ListBikeItem;
