import { mockRentBike } from '../../mockRentBike/mockRentBike'
import {  mockList } from '../../mockList/mockList'
import { removeElementById, addElement, selectElement, findElementById, addItemToList } from '../../Utils/bikeUtils'

const initialState = {
    list: mockList,
    rent: mockRentBike,
    total: 0,
    quantity: 0,
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'CREATE_ITEM': {
            const newList =  addItemToList(action.item, state.list)
            return {
                ...state,
                list: newList
            }
        }
        case 'REMOVE_ITEM':{
            return {
                ...state,
                list: removeElementById(action.itemId, state.list)
            }
        }
        case 'SELECT_ITEM_FOR_RENT': {
            const element = findElementById(action.itemId, state.list)
            const newList = selectElement(action.itemId, state.list)
            return {
                ...state,
                list: newList,
                rent: addElement(element, state.rent)
            }
        }
        case 'END_OF_RENT': {
            const element = findElementById(action.itemId, state.rent)
            const newList = selectElement(action.itemId, state.rent)
            return {
                ...state,
                list: addElement(element, state.list),
                rent: newList,
            }
        }
        default:
            return state
    }
};

export default reducer;
