import React, { useState } from 'react';

import './RentBikeItem.css';

const RentBikeItem = ({items, onCancel}) => {

    const [countTime, setCountTime] = useState(0);
    const [countTenSeconds, setCountTenSeconds] = useState(0)
    const [countOneMin, setCountOneMin] = useState(0);
    const [countTenMin, setCountTenMin] = useState(0);
    const [countOneHour, setCountOneHour] = useState(0);
    const [countTenHour, setCountTenHour] = useState(0);


    const startTime = () => {
        let timeLeft = countTime;
        let tenSecond = countTenSeconds;
        let oneMin = countOneMin;
        let tenMin = countTenMin;
        let oneHour = countOneHour;
        let tenHour = countTenHour
        let time = setInterval(() => {
            timeLeft++            
            if(timeLeft === 10) {
                timeLeft = 0
                tenSecond++
                setCountTenSeconds(tenSecond)
            } else if(tenSecond === 6) {
                timeLeft = 0;
                tenSecond = 0;
                setCountTenSeconds(tenSecond)
                oneMin++
                setCountOneMin(oneMin)
            } else if(oneMin === 10) {
                timeLeft = 0;
                tenSecond = 0;
                oneMin = 0;
                setCountTenSeconds(tenSecond)
                setCountOneMin(oneMin)
                tenMin++
                setCountTenMin(tenMin)
            } else if(tenMin === 6) {
                timeLeft = 0;
                tenSecond = 0;
                oneMin = 0;
                tenMin = 0;
                setCountTenSeconds(tenSecond)
                setCountOneMin(oneMin)
                setCountTenMin(tenMin)
                oneHour++
                setCountOneHour(oneHour)
            } else if(oneHour === 10) {
                timeLeft = 0;
                tenSecond = 0;
                oneMin = 0;
                tenMin = 0;
                oneHour = 0
                setCountTenSeconds(tenSecond)
                setCountOneMin(oneMin)
                setCountTenMin(tenMin)
                setCountOneHour(oneHour)
                tenHour++
                setCountTenHour(tenHour)
            }
            setCountTime(timeLeft)
        }, 1)
    } 
    
    const { name, type, price } = items;

    return (
        <div className="rentBikeItem itemBike d-flex">
            <p>{`${name} / ${type} / $${price}`}</p>
            <span>{countOneHour}:{countTenMin}{countOneMin}:{countTenSeconds}{countTime}</span>
            <button className="btn btn-danger float-right" onClick={startTime}>Cancel rent</button>
        </div>
    );
};

export default RentBikeItem;
